import React, { useEffect, useState } from 'react';
import './index.scss';

function BarChart(props) {
    const [height, setHeight] = useState(0);
    const [width, setWidth] = useState(0);

    const startX = 30;
    const startY = 30;
    useEffect( () => {
        const interval = setInterval( () => {
            const height = document.getElementById('bar-chart-container')?.clientHeight;
            const width = document.getElementById('bar-chart-container')?.clientWidth;
            if(height && width) {
                setHeight(height);
                setWidth(width);
                clearInterval(interval);
            }
        }, 500);
    }, []);

    const data = [10, 20, 30, 40, 23, 31];
    return (
        <div className="bar-chart-container" id="bar-chart-container">
            <svg width="100%" height="100%">
                 {/* <path d={`M 30 30 V ${height - 30} H ${width - 30}`} fill="none" strokeWidth="2" stroke="red"/> */}
                 {/** Y Axis */}
                 <line 
                    x1={startX} 
                    y1={startY} 
                    y2={height - 30} 
                    x2={startX} 
                    stroke="black" 
                    strokeWidth="2" 
                    strokeLinecap="butt"/>

                {/** X Axis */}
                <line 
                    x1={startX} 
                    y1={height - 30} 
                    y2={height - 30} 
                    x2={width - 30} 
                    stroke="black" 
                    strokeWidth="2" 
                    strokeLinecap="butt"/>

                <rect x="60" y={height - 130} width="10" height="100" fill="blue" />

            </svg>
        </div>
    );
}

export default BarChart;
